import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import axios from "axios"; 
import { makeStyles } from "@material-ui/core/styles";
import { Button, Nav, Navbar, Container, Row, Col, Form, ListGroup } from 'react-bootstrap';
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import './loader.css';


function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`nav-tabpanel-${index}`}
      aria-labelledby={`nav-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired
};

function a11yProps(index) {
  return {
    id: `nav-tab-${index}`,
    "aria-controls": `nav-tabpanel-${index}`
  };
}

function LinkTab(props) {
  return (
    <Tab
      component="a"
      onClick={(event) => {
        event.preventDefault();
      }}
      {...props}
    />
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper
  },
  table: {
    minWidth: 650,
  },
}));

export default function NavTabs() {



  const [input, setInput] = useState('');
  const [data, setData] = useState(['loading']); 

  const handleOnClick=()=>
  {
    axios.post("http://127.0.0.1:8000/post", {tag: input}).then((response)=>{
      setData(response.data); 
    })
  }

  const [data2, setData2] = useState(['....loading']); 

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/history")
      .then(response => setData2(response.data));

  }, []);



  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Tabs
          variant="fullWidth"
          value={value}
          onChange={handleChange}
          aria-label="nav tabs example"
        >
          <LinkTab label="Crawl" href="/drafts" {...a11yProps(0)} />
          <LinkTab label="History" href="/trash" {...a11yProps(1)} />
        </Tabs>
      </AppBar>
      <TabPanel value={value} index={0}>

      <Row>
    <Col xs={7}>
      <Form.Control placeholder="User" onChange = {(e)=> setInput(e.target.value)}/>
    </Col>
    <Col>
    <Button variant="flat" size="l" onClick={()=> handleOnClick()} > Crawl Tags</Button>
    </Col>

  </Row>

  <Container  className="container-loader">
  <Row>
    <Col>
    <ListGroup>

</ListGroup>

<br></br>1 of 1<br></br>

<Button variant="flat" size="s" className="next"> Crawl More</Button></Col>
  </Row>



<TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Title</TableCell>
            <TableCell align="right">Details</TableCell>
            <TableCell align="right">Author</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>

        {
    data.map((newData)=>{
    return (
      <ListGroup.Item>{newData.title}, {newData.author}, {newData.details} </ListGroup.Item>
    )
  })};
       </TableBody>
      </Table>
    </TableContainer>
    </Container>

      </TabPanel>
      <TabPanel value={value} index={1}>

      <Container className="container-loader">


<TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Tags</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>

        {
    data.map((newData)=>{
    return (
      <ListGroup.Item>{newData.tag}</ListGroup.Item>
    )
  })};

        </TableBody>
      </Table>
    </TableContainer>




</Container>

      </TabPanel>
    </div>
  );
}
